function getPositionFromElementAtOffset(element,x,y,z)   if not x or not y or not z then      return false   end		local ox,oy,oz = getElementPosition(element)        local matrix = getElementMatrix ( element )		if not matrix then return ox+x,oy+y,oz+z end        local offX = x * matrix[1][1] + y * matrix[2][1] + z * matrix[3][1] + matrix[4][1]        local offY = x * matrix[1][2] + y * matrix[2][2] + z * matrix[3][2] + matrix[4][2]        local offZ = x * matrix[1][3] + y * matrix[2][3] + z * matrix[3][3] + matrix[4][3]        return offX, offY, offZend