local isDrawing = falselocal lx,ly,lzlocal function drawingTimeLeft()	if(not isDrawing)then		return	end	isDrawing = falseendfunction callDrawing(vID,pos,color)	if(pos[1] == lx and pos[2] == ly and pos[3] == lz)then return end	if(not isDrawing)then 		isDrawing = true		setTimer(drawingTimeLeft, 500, 1)		triggerServerEvent("requestCreateVehicle", getLocalPlayer(),vID, pos,color)	end	lx = pos[1] ly = pos[2] lz = pos[3]	--outputChatBox("Draw")end